import React, { useState } from "react";
import { AwesomeButton } from "react-awesome-button";
import 'react-awesome-button/dist/themes/theme-c137.css';
import InfoIcon from "@material-ui/icons/Info";
import SchoolIcon from "@material-ui/icons/School";
import WorkIcon from "@material-ui/icons/Work";
import ProjectsIcon from "@material-ui/icons/GitHub";
import SkillsIcon from "@material-ui/icons/LocalActivity";


export default function AddField(props) {

    const [field, setField,] = useState({
      title: "21312",
      input1:"",
      input2:"",
      input3:"",
      desc1:"",
    });


    function handleChange(event) {
      const {name, value} = event.target; /* we store the change, and set the note as that change. */
                                          /* So to summarise, we update a variable as we type(change) */
      setField(prevField => {
        return {
          ...prevNote,
          [name]: value
        };
      });
      console.log("mouse is over")
     }

    function submitField(title,input1,input2,input3,desc) {

      setField({
        title: title,
        input1: input1,
        input2: input2,
        input3: input3,
        desc: desc,
      });
      props.onAdd(field);
    }


    var buttons = [{title:'Bio' ,input1:'Full Name',input2:'Email',input3:'Mobile',desc:'Github',icon:<InfoIcon/>},

                   {title:'Education',input1:'University',input2:'Degree',input3:'Location',desc:'Duration',icon:<SchoolIcon/>},

                   {title:'Work History',input1:'Company and Role',input2:'Location',input3:'Duration',desc:'Description',icon:<WorkIcon/>},

                   {title:'Projects',input1:'Title',input2:'Technology',input3:'Link',desc:'Description',icon:<ProjectsIcon/>},

                   {icon:<SkillsIcon/>}]
    return (
      <div>
        {buttons.map((item) => (
            <button
            onClick={() => submitField(item.title, item.input1, item.input2, item.input3, item.desc)}
            onMouseOver= {handleChange}
            style={{margin:'5px', }}
            type="primary">
             {item.icon}
            </button>
        ))}
     </div>
    )
};
