import React, { useState } from "react";
import { AwesomeButton } from "react-awesome-button";
import 'react-awesome-button/dist/themes/theme-c137.css';
import AddField from "./AddField";
import Field from "./Field";
import { PDFViewer } from '@react-pdf/renderer';
import { Page, Text, View, Document, StyleSheet } from '@react-pdf/renderer';

const test = [{"Task":"Coding","Hours":2,"Rate":72},
{"Task":"Testing","Hours":2,"Rate":72},
{"Task":"Meeting","Hours":2,"Rate":72},
{"Task":"Deployment","Hours":2,"Rate":72},
{"Task":"UAT","Hours":2,"Rate":72},
{"Task":"PROD","Hours":2,"Rate":72},
{"Task":"DEV","Hours":2,"Rate":72},
{"Task":"Yelling","Hours":2,"Rate":72},
{"Task":"New features","Hours":2,"Rate":72},
{"Task":"Unrelated","Hours":2,"Rate":72},
{"Task":"Admin","Hours":2,"Rate":72}];

const styles = StyleSheet.create({
  page: {
    flexDirection: 'row',
    backgroundColor: 'red',

  },
  section: {
    flexDirection: 'row',
    backgroundColor: 'blue',
    padding: 10,
    flexGrow: 1,
    height: '25%',
    display: 'block'
  },
});


const MyDocument = () => (
  <Document>
    <Page size="B1" style={styles.page}>
          {test.map(item => (
            <View style={styles.section}>
            <Text>
                {item.Task}
                {item.Hours}
                {item.Rate}{'\n\n'}
          </Text>
          </View>
        ))}
    </Page>
  </Document>
);

export default function App() {

  const [fields, setFields] = useState([]);

  function addField(newField) {
    setFields(prevFields => {
      return [...prevFields, newField];
    });
  }

  function deleteNote(id) {
    setFields(prevFields => {
      return prevFields.filter((item, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div className ="row">

   {fields.length > 0 &&
      <div className= "column2">
        <PDFViewer height= {800} width = {500}>
          <MyDocument />
        </PDFViewer>
      </div>
   }
      <div className= "column1">
        <AddField onAdd = {addField}/>
        {fields.map((item, index) => {
          return (
            <Field
              key = {index}
              id = {index}
              title ={item.title}
              input1 = {item.input1}
              input2 = {item.input2}
              input3 = {item.input3}
              desc =  {item.desc}
              onDelete = {deleteNote}
            />
          );
        })}
        {fields.length > 0 && <AwesomeButton> Submit </AwesomeButton>}
      </div>
    </div>
  );
}
