import React, { useState } from "react";
import { AwesomeButton } from "react-awesome-button";
import 'react-awesome-button/dist/themes/theme-c137.css';
import AddField from "./AddField";
import Field from "./Field";
import { PDFViewer } from '@react-pdf/renderer';
import Resume from "./Resume";


export default function App() {

  const [fields, setFields] = useState([]);

  function addField(newField) {
    setFields(prevFields => {
      return [...prevFields, newField];
    });
  }

  function deleteNote(id) {
    setFields(prevFields => {
      return prevFields.filter((item, index) => {
        return index !== id;
      });
    });
  }

  function handleChange(dictionary) {
    setFields({input1: dictionary["input1"], input2: dictionary["input2"], input3: dictionary["input3"], desc: dictionary["desc"]});
  }

  return (

    <div className ="row">
      <div className= "column2">
        <PDFViewer height= {800} width = {500}>
          <Resume />
        </PDFViewer>
      </div>
      <div className= "column1">
        <AddField onAdd = {addField}/>
        {fields.map((item, index) => {
          return (
            <Field
              key = {index}
              id = {index}
              title ={item.title}
              input1 = {item.input1}
              input2 = {item.input2}
              input3 = {item.input3}
              desc =  {item.desc}
              onDelete = {deleteNote}
              onChange = {handleChange}
            />
          );
        })}
        {fields.length > 0 && <AwesomeButton onPress={console.log(fields)}> Submit </AwesomeButton>}
      </div>
    </div>
  );
}
