import React, { useState } from "react";
import { AwesomeButton } from "react-awesome-button";
import 'react-awesome-button/dist/themes/theme-c137.css';
import InfoIcon from "@material-ui/icons/Info";
import SchoolIcon from "@material-ui/icons/School";
import WorkIcon from "@material-ui/icons/Work";
import ProjectsIcon from "@material-ui/icons/GitHub";
import SkillsIcon from "@material-ui/icons/LocalActivity";


export default function AddField(props) {

    const [field, setField,] = useState({
      title: "",
      content: ""
    });

    function submitField(event) {
      props.onAdd(field);
      setField({
        title: "",
        content: ""
      });
      event.preventDefault();
    }


    var buttons = [{icon:<InfoIcon/>},{icon:<SchoolIcon/>}, {icon:<WorkIcon/>},{icon:<ProjectsIcon/>},{icon:<SkillsIcon/>}]
    return (
      <div>
        {buttons.map((item) => (
            <button
            onClick = {submitField}
            style={{margin:'5px', }}
            type="primary">
            {item.icon}
            </button>
        ))}
     </div>
    )
};
